mapper.delete:
delete from contract
where taxi_id = #{taxiId,jdbcType=INTEGER}
  and driver_id = #{driverId,jdbcType=CHAR}

mapper.update:
update contract
set create_time = #{createTime,jdbcType=TIMESTAMP},
  end_time = #{endTime,jdbcType=TIMESTAMP},
  deposit = #{deposit,jdbcType=INTEGER}
where taxi_id = #{taxiId,jdbcType=INTEGER}
  and driver_id = #{driverId,jdbcType=CHAR}

mapper.select:
select taxi_id, driver_id, create_time, end_time, deposit
from contract
where taxi_id = #{taxiId,jdbcType=INTEGER}
  and driver_id = #{driverId,jdbcType=CHAR}
