mapper.update:
update contract
taxi_id = #{taxiId,jdbcType=INTEGER}, driver_id = #{driverId,jdbcType=CHAR}, create_time = #{createTime,jdbcType=TIMESTAMP}, end_time = #{endTime,jdbcType=TIMESTAMP}, deposit = #{deposit,jdbcType=INTEGER}
where id = #{id,jdbcType=CHAR}

mapper.select:
select id, taxi_id, driver_id, create_time, end_time, deposit
from contract
where id = #{id,jdbcType=CHAR}
