mapper.update:
update driver
name = #{name,jdbcType=VARCHAR}, gender = #{gender,jdbcType=CHAR}, password = #{password,jdbcType=VARCHAR}, phone = #{phone,jdbcType=CHAR}, license_num = #{licenseNum,jdbcType=CHAR}, identity_num = #{identityNum,jdbcType=CHAR},
where id = #{id,jdbcType=CHAR}

mapper.select:
select id, name, gender, password, phone, license_num, identity_num
from driver
where id = #{id,jdbcType=CHAR}
